// src/components/PostItemPage.js
import React, { useState } from 'react';
import axios from 'axios';

const PostItemPage = () => {
  const [itemName, setItemName] = useState('');
  const [itemDescription, setItemDescription] = useState('');
  const [itemStartingPrice, setItemStartingPrice] = useState('');
  const [selectedImageFile, setSelectedImageFile] = useState(null);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleImageChange = (e) => {
    setSelectedImageFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('imageFile', selectedImageFile);
    formData.append('name', itemName);
    formData.append('description', itemDescription);
    formData.append('startingPrice', itemStartingPrice);

    axios.post('https://localhost:7137/api/auction/upload', formData)
      .then(response => {
        console.log('Item added with image:', response.data);
        setIsSubmitted(true); // Flag to show success message
      })
      .catch(error => {
        console.error('Error uploading item with image:', error);
      });
  };

  return (
    <div>
      <h1>Post a New Auction Item</h1>

      {isSubmitted ? (
        <p>Item posted successfully!</p>
      ) : (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Item Name"
            value={itemName}
            onChange={(e) => setItemName(e.target.value)}
            required
          />
          <input
            type="text"
            placeholder="Item Description"
            value={itemDescription}
            onChange={(e) => setItemDescription(e.target.value)}
            required
          />
          <input
            type="number"
            placeholder="Starting Price"
            value={itemStartingPrice}
            onChange={(e) => setItemStartingPrice(e.target.value)}
            required
          />
          <input
            type="file"
            onChange={handleImageChange}
            accept="image/*"
            required
          />
          <button type="submit">Post Item</button>
        </form>
      )}
    </div>
  );
};

export default PostItemPage;
