// src/App.js
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import HomePage from './components/HomePage';
import AddItemPage from './components/AddItemPage';
import LoginPage from './components/LoginPage';
import SignupPage from './components/SignupPage';
import AdminDashboard from './components/AdminDashboard'; // Add an admin dashboard component

function App() {
  const [user, setUser] = useState(null); // Tracks user data and role
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate fetching user data from an API or local storage
    const fetchUser = () => {
      const storedUser = JSON.parse(localStorage.getItem('user'));
      if (storedUser) {
        setUser(storedUser);
      }
      setLoading(false);
    };
    fetchUser();
  }, []);

  const isAdmin = user?.role === 'admin';

  if (loading) return <div>Loading...</div>;

  return (
    <Router>
      <Routes>
        {/* Default Home Page */}
        <Route path="/" element={<HomePage />} />

        {/* Public Routes */}
        <Route path="/login" element={<LoginPage setUser={setUser} />} />
        <Route path="/signup" element={<SignupPage />} />

        {/* Admin Protected Route */}
        <Route
          path="/admin"
          element={isAdmin ? <AdminDashboard /> : <Navigate to="/" />}
        />

        {/* Add Item Route (only accessible if logged in) */}
        <Route
          path="/add-item"
          element={user ? <AddItemPage /> : <Navigate to="/login" />}
        />

        {/* Redirect new users to Signup */}
        <Route
          path="*"
          element={<Navigate to={user ? "/" : "/signup"} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
