// src/components/HomePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    // Fetch auction items from the backend
    axios.get('https://localhost:7137/api/auction')
      .then(response => {
        setItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching auction items:', error);
      });
  }, []);

  return (
    <div>
      <h1>Jewelry Auction</h1>

      {/* Link to navigate to PostItemPage */}
      <Link to="/post-item">
        <button className="post-item-button">Post a New Item</button>
      </Link>

      <div className="items-container">
        {items.length > 0 ? (
          items.map(item => (
            <div key={item.id} className="auction-item">
              <h2>{item.name}</h2>
              <p>{item.description}</p>
              <p>Starting Price: ${item.startingPrice}</p>

              {/* Display the image if it exists */}
              {item.imageUrl && (
                <img 
                  src={item.imageUrl} 
                  alt={item.name} 
                  className="item-image" 
                />
              )}
            </div>
          ))
        ) : (
          <p>No items available for auction.</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
