// src/components/HomePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css';

const HomePage = () => {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [sortOption, setSortOption] = useState('name'); // Default sort by name

  useEffect(() => {
    // Fetch auction items from the backend
    const fetchItems = async () => {
      try {
        const response = await axios.get('https://localhost:7137/api/auction');
        setItems(response.data);
      } catch (error) {
        console.error('Error fetching auction items:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchItems();
  }, []);

  // Handle search input change
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Handle sort option change
  const handleSortChange = (event) => {
    setSortOption(event.target.value);
  };

  // Filter and sort items based on search query and sort option
  const filteredItems = items
    .filter(item => item.name.toLowerCase().includes(searchQuery.toLowerCase()))
    .sort((a, b) => {
      if (sortOption === 'name') {
        return a.name.localeCompare(b.name);
      } else if (sortOption === 'price') {
        return a.startingPrice - b.startingPrice;
      }
      return 0;
    });

    const formData = new FormData();
formData.append('imageFile', selectedImageFile); // selectedImageFile is the image file from input
formData.append('name', itemName); // Other fields from the form
formData.append('description', itemDescription);
formData.append('startingPrice', itemStartingPrice);

axios.post('https://localhost:7137/api/auction/upload', formData)
  .then(response => {
    console.log('Item added with image:', response.data);
  })
  .catch(error => {
    console.error('Error uploading item with image:', error);
  });



  return (
    <div className="homepage">
      
      <h1>Jewelry Auction</h1>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search for items..."
          value={searchQuery}
          onChange={handleSearchChange}
        />
      </div>

      {/* Sort Options */}
      <div className="sort-options">
        <label htmlFor="sort">Sort by:</label>
        <select id="sort" value={sortOption} onChange={handleSortChange}>
          <option value="name">Name</option>
          <option value="id">ID</option>
          <option value="price">Price</option>
        </select>
      </div>

      {/* Loading Spinner */}
      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <div className="items-container">
          {filteredItems.length > 0 ? (
            filteredItems.map(item => (
              <div key={item.id} className="auction-item">
                <img src={item.imageUrl || '/placeholder.jpg'} alt={item.name} className="item-image" />
                <h2>{item.id}</h2>
                <h2>{item.name}</h2>
                <p>{item.description}</p>
                <p>Starting Price: ${item.startingPrice}</p>
                <button className="bid-button">Place Bid</button>
              </div>
            ))
          ) : (
            <p>No items available for auction.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default HomePage;
