// src/components/HomePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Navbar.css'
import { FaBars } from "react-icons/fa6";
import { FaCircleUser } from "react-icons/fa6";

const HomePage = () => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    // Fetch auction items from the backend
    axios.get('https://localhost:7137/api/auction')
      .then(response => {
        setItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching auction items:', error);
      });
  }, []);

  return (

    <div>

      //Navbar
      <div>
        <div className='navbar'>
            <div className="nav-logo">
                <p>The Auction Room</p>
            </div>
            <ul className={`nav-menu ${isOpen ? 'nav-menu-active' : ''}`}>
                <li>Auctions</li>
                <li>Sell with us</li>
                <li>About</li>
                <li>Contact</li>
            </ul>
            <div className="nav-login-user">
                <FaCircleUser size={25} />
                
                <div className="nav-icon" onClick={toggleMenu}>
                    <FaBars size={25} />
                </div>
            </div>
            
        </div>
    </div>


      <h1>Jewelry Auction</h1>
      <div className="items-container">
        {items.length > 0 ? (
          items.map(item => (
            <div key={item.id} className="auction-item">
              <h2>{item.id}</h2>
              <h2>{item.name}</h2>
              <p>{item.description}</p>
              <p>Starting Price: ${item.startingPrice}</p>
            </div>
          ))
        ) : (
          <p>No items available for auction.</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
