import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ViewTables = () => {
  const [tables, setTables] = useState([]);
  const [selectedTable, setSelectedTable] = useState(null);
  const [tableData, setTableData] = useState([]);

  // Function to fetch the list of tables
  const fetchTables = async () => {
    try {
      const response = await axios.get('/api/tables'); // Adjust API endpoint
      setTables(response.data);
    } catch (error) {
      console.error('Error fetching tables:', error);
    }
  };

  // Function to fetch data from a specific table
  const fetchTableData = async (tableName) => {
    try {
      const response = await axios.get(`/api/tables/${tableName}`); // Adjust API endpoint
      setTableData(response.data);
      setSelectedTable(tableName);
    } catch (error) {
      console.error('Error fetching table data:', error);
    }
  };

  useEffect(() => {
    fetchTables();
  }, []);

  return (
    <div>
      <h1>View Tables</h1>
      <h2>Select a Table</h2>
      <ul>
        {tables.map((table) => (
          <li key={table} onClick={() => fetchTableData(table)}>
            {table}
          </li>
        ))}
      </ul>
      
      {selectedTable && (
        <div>
          <h2>Data from {selectedTable}</h2>
          <button onClick={() => fetchTableData(selectedTable)}>View Details</button>
          <table>
            <thead>
              <tr>
                {tableData.length > 0 && Object.keys(tableData[0]).map((key) => (
                  <th key={key}>{key}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {tableData.map((row, index) => (
                <tr key={index}>
                  {Object.values(row).map((value, idx) => (
                    <td key={idx}>{value}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ViewTables;
