import React, { Component } from "react";
import "./SellWithUs.css";
import "../Profile/Navbar.css";
import { FaBars, FaCircleUser } from 'react-icons/fa6';
import { Link } from 'react-router-dom';

export default class SellWithUs extends Component {
  state = {
    itemName: "",
    price: 0,
    message: "",
    filePath: "", // This will hold the image URL
    errors: {},
    isOpen: false, // Added state for menu toggle
  };

  // Toggle menu for responsive design
  toggleMenu = () => {
    this.setState((prevState) => ({ isOpen: !prevState.isOpen }));
  };

  // Handle text input change and validate fields
  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value }, () => {
      this.validateField(name, value);
    });
  };

  // Handle file selection and set the file path URL
  handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const IMAGE_BASE_URL = "http://localhost:3000/images/"; // Adjust to your local server URL
      const fileUrl = `${IMAGE_BASE_URL}${file.name}`;
      this.setState({ filePath: fileUrl });
    }
  };

  // Handle form submission
  handleSubmit = async (e) => {
    e.preventDefault();

    if (!this.isFormValid()) {
      alert("Please fix the errors before submitting.");
      return;
    }

    try {
      const response = await fetch("https://localhost:7137/api/SellWithUs/submit", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          itemName: this.state.itemName,
          price: this.state.price,
          message: this.state.message,
          filePath: this.state.filePath,
        }),
      });

      if (response.ok) {
        alert("Item submitted successfully!");
        this.setState({ itemName: "", price: 0, message: "", filePath: "", errors: {} });
      } else {
        const errorData = await response.json();
        alert(`Submission failed: ${errorData.message || "Unknown error."}`);
      }
    } catch (error) {
      console.error("Error submitting the form:", error);
      alert("There was an error submitting your item. Please try again.");
    }
  };

  // Validate individual fields
  validateField = (name, value) => {
    let errors = this.state.errors;

    if (name === "itemName" && value.length < 3) {
      errors.itemName = "Item name must be at least 3 characters long.";
    } else {
      errors.itemName = "";
    }

    if (name === "price" && value <= 0) {
      errors.price = "Price must be greater than 0.";
    } else {
      errors.price = "";
    }

    this.setState({ errors });
  };

  // Check if the form is valid
  isFormValid = () => {
    const { itemName, price, message, errors } = this.state;
    return (
      itemName &&
      price > 0 &&
      message &&
      !Object.values(errors).some((error) => error !== "")
    );
  };

  render() {
    const { errors, isOpen } = this.state;

    return (
      <div>
        {/* Navbar */}
        <div className="navbar">
          <div className="nav-logo">
            <p>
              <Link to="/HomePage" className="logo-home">
                The Auction Room
              </Link>
            </p>
          </div>
          <ul className={`nav-menu ${isOpen ? 'nav-menu-active' : ''}`}>
            <li>
              <Link to="/Auctions" className="user-nav">Auctions</Link>
            </li>
            <li>
              <Link to="/sell-with-us" className="user-nav">Sell with us</Link>
            </li>
            <li>
              <Link to="/aboutus" className="user-nav">About</Link>
            </li>
            <li>
              <Link to="/contact-us" className="user-nav">Contact</Link>
            </li>
          </ul>
          <div className="nav-login-user">
            <Link to="/MyAccount" className="user-nav">
              <FaCircleUser size={25} />
            </Link>
            <div className="nav-icon" onClick={this.toggleMenu}>
              <FaBars size={25} />
            </div>
          </div>
        </div>

        {/* Sell With Us Section */}
        <div className="sell-with-us-section">
          <h1>Sell With Us</h1>
        </div>

        <div className="responsive-paragraph">
          <span className="xyz">
            Do you have an item you would like to sell? Leave your details and we will contact you shortly.
          </span>
        </div>

        {/* Form Section */}
        <section className="contact">
          <form onSubmit={this.handleSubmit}>
            <div className="form-group-row">
              <div className="input-box">
                <label>Item Name</label>
                <input
                  type="text"
                  className="field"
                  name="itemName"
                  value={this.state.itemName}
                  onChange={this.handleInputChange}
                  required
                />
                {errors.itemName && <span className="error">{errors.itemName}</span>}
              </div>

              <div className="input-box">
                <label>Price ($)</label>
                <input
                  type="text"
                  className="field"
                  name="price"
                  value={this.state.price}
                  onChange={this.handleInputChange}
                  required
                />
                {errors.price && <span className="error">{errors.price}</span>}
              </div>
            </div>

            <div className="input-box">
              <label>Message</label>
              <textarea
                name="message"
                className="field"
                value={this.state.message}
                onChange={this.handleInputChange}
                required
              />
              {errors.message && <span className="error">{errors.message}</span>}
            </div>

            <div className="input-box">
              <label>Upload File</label>
              <input
                type="file"
                id="file"
                name="file"
                onChange={this.handleFileChange}
              />
            </div>

            <div>
              <button type="submit" className="sellWithUs-button">Send</button>
            </div>
          </form>
        </section>

        {/* Footer */}
        <div className="footer">
          <div className="first">
            <h1>The Auction Room</h1>
            <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry.</p>
          </div>
          <div className="second">
            <ul>
              <li>Auctions</li>
              <li>Past Auctions</li>
              <li>Profile</li>
              <li>Sell with us</li>
            </ul>
          </div>
          <div className="third">
            <ul>
              <li>About</li>
              <li>Contact</li>
              <li>Terms & Conditions</li>
              <li>Privacy Policy</li>
            </ul>
          </div>
        </div>
      </div>
    );
  }
}
