import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [items, setItems] = useState([]);
    const [isShuttingDown, setIsShuttingDown] = useState(false);

    const fetchUsers = async () => {
        try {
            const response = await axios.get('https://localhost:7137/api/user'); // Replace with your endpoint
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const fetchItems = async () => {
        try {
            const response = await axios.get('https://localhost:7137/api/auction'); // Replace with your endpoint
            setItems(response.data);
        } catch (error) {
            console.error('Error fetching items:', error);
        }
    };

    const deleteUser = async (userId) => {
        await axios.delete(`https://localhost:7137/api/user/${userId}`); // Replace with your endpoint
        fetchUsers(); // Refresh users after deletion
    };

    const makeAdmin = async (userId) => {
        await axios.patch(`https://localhost:7137/api/user/${userId}/makeAdmin`); // Replace with your endpoint
        fetchUsers(); // Refresh users after updating
    };

    const deleteItem = async (itemId) => {
        await axios.delete(`https://localhost:7137/api/auction/${itemId}`); // Replace with your endpoint
        fetchItems(); // Refresh items after deletion
    };

    const changeItemDetails = async (itemId, updatedDetails) => {
        await axios.put(`https://localhost:7137/api/auction/${itemId}`, updatedDetails); // Replace with your endpoint
        fetchItems(); // Refresh items after updating
    };

    const toggleWebsiteStatus = async () => {
      await axios.patch('http://localhost:7173/api/settings/toggleMaintenance'); // Adjust endpoint as necessary
      setIsShuttingDown(prevState => !prevState);
  };

  // To get the current maintenance status when the component loads
const fetchMaintenanceStatus = async () => {
  try {
      const response = await axios.get('http://localhost:7173/api/settings/status'); // Adjust endpoint as necessary
      setIsShuttingDown(response.data.IsMaintenanceMode);
  } catch (error) {
      console.error('Error fetching maintenance status:', error);
  }
};

useEffect(() => {
  fetchUsers();
  fetchItems();
  fetchMaintenanceStatus();
}, []);


  

    return (
        <div>
            <h1>Admin Dashboard</h1>

            <h2>Manage Users</h2>
            <ul>
                {users.map(user => (
                    <li key={user.id}>
                        {user.name} 
                        <button onClick={() => makeAdmin(user.id)}>Make Admin</button>
                        <button onClick={() => deleteUser(user.id)}>Delete User</button>
                    </li>
                ))}
            </ul>

            <h2>Manage Items</h2>
            <ul>
                {items.map(item => (
                    <li key={item.id}>
                        {item.name} 
                        <button onClick={() => changeItemDetails(item.id, { /* new details */ })}>Change Details</button>
                        <button onClick={() => deleteItem(item.id)}>Delete Item</button>
                    </li>
                ))}
            </ul>

            <button onClick={toggleWebsiteStatus}>
                {isShuttingDown ? 'Shutdown Website' : 'Reopen Website'}
            </button>
        </div>
    );
};

export default AdminDashboard;
